import { createApi, fetchBaseQuery } from '@reduxjs/toolkit/query/react'
import { Stub } from '../types'

export type PokemonType = {
    number: Number;
    types: 
    {
        slot: Number;
        type: Stub;
    }[];

    height: Number;
    weight: Number;

    sprites: {}[];

    abilities: 
    {

    }[];
}

/*
    I've confirmed this works.
*/
export const getPokemon = async () => {
    const tempFunc = async ()=>{
        const response = await fetch("https://pokeapi.co/api/v2/pokemon?limit=151&offset=0");
        const json = await response.json();

        const data = (json.results) as PokemonStubType[];

        return data;
    };

    let data=(await tempFunc());

    let results: PokemonType[] = [];

    for(let i = 0; i<data.length; i++)
    {
        const response = await fetch(data[i].url);
        const json = await response.json();

        /*
        On a notable technicality, this information isn't static or baked in.
        It's just grabbing *specifically the data we wanted* from our request.
        More of a filtering step than aything else.
        */
        results[i] = {
            number: json.id,
            types: json.types,
            height: json.height,
            weight: json.weight,
            sprites: [json.sprites.versions["generation-i"], json.sprites.versions["generation-ii"], json.sprites.versions["generation-iii"], json.sprites.versions["generation-iv"], json.sprites.versions["generation-v"], json.sprites.versions["generation-vi"]],
            abilities: json.abilities,
        };
    }

    return results as PokemonType[];
}


export const pokemonApi = createApi({
  reducerPath: 'pokemonApi',
  baseQuery: fetchBaseQuery({ baseUrl: 'https://pokeapi.co/api/v2/' }),
  endpoints: builder => ({
    getPokemonById: builder.query({
      query: (id: string) => `pokemon/${id}`
    })
  })
})

export const { useGetPokemonByIdQuery } = pokemonApi
