import { useGetPokemonByIdQuery } from "./counter/counterAPI.tsx";
import { useState } from "react";

export const Pokemon = ({id, children})=>
{   
    const { rawData, error, isLoading } = useGetPokemonByIdQuery(id);
    const data = {
        number: rawData.id,
        types: rawData.types,
        height: rawData.height,
        weight: rawData.weight,
        sprites: [rawData.sprites.versions["generation-i"], rawData.sprites.versions["generation-ii"], rawData.sprites.versions["generation-iii"], rawData.sprites.versions["generation-iv"], rawData.sprites.versions["generation-v"], rawData.sprites.versions["generation-vi"]],
        abilities: rawData.abilities,
    };

    return (
        <div>
            {JSON.stringify(data)}
        </div>
    );
}

export default Pokemon;
