import React from 'react';
import { useEffect, useState } from "react";
import { createApi, fetchBaseQuery } from '@reduxjs/toolkit/query/react'
import { PokeProfile } from './types';

export const pokemonApi = createApi({
  reducerPath: 'pokemonApi',
  baseQuery: fetchBaseQuery({ baseUrl: 'https://pokeapi.co/api/v2/' }),
  endpoints: builder => ({
    getPokemonById: builder.query({
      query: (id: string) => `pokemon/${id}`
    })
  })
})
export const { useGetPokemonByIdQuery } = pokemonApi


export const Pokemon = ({id, children})=>
{       
    const [cachedData, setCachedData] = useState({});

    const getData = async (query)=>{
        const {data, error, isLoading} = query(id);
        await data;
        return data as PokeProfile[];
    }

    const [gen, setGen] = useState(1);
    const [url, setUrl] = useState("");

    return (
        <div>
            <button onClick={()=>
                {
                    const {data, error, isLoading} = useGetPokemonByIdQuery(id);
                }
            }>
                Load Pokemon Number {id}
            </button>

            <button onClick={()=>{setUrl(""); setGen(1);}}>1</button>
            <button onClick={()=>{setUrl(""); setGen(2);}}>2</button>
            <button onClick={()=>{setUrl(""); setGen(3);}}>3</button>
            <button onClick={()=>{setUrl(""); setGen(4);}}>4</button>
            <button onClick={()=>{setUrl(""); setGen(5);}}>5</button>
            <button onClick={()=>{setUrl(""); setGen(6);}}>6</button>
            <img src={url}></img>
        </div>
    );
}

export default Pokemon;
