import React from 'react';
import { useEffect, useState } from "react";
import { createApi, fetchBaseQuery } from '@reduxjs/toolkit/query/react'
import { PokeProfile } from './types';

export const pokemonApi = createApi({
  reducerPath: 'pokemonApi',
  baseQuery: fetchBaseQuery({ baseUrl: 'https://pokeapi.co/api/v2/' }),
  endpoints: builder => ({
    getPokemonById: builder.query({
      query: (id: string) => `pokemon/${id}`
    })
  })
})

export const { useGetPokemonByIdQuery } = pokemonApi

export const Pokemon = ({id, children})=>
{       
    const [cachedData, setCachedData] = useState({});
    const [gen, setGen] = useState(1);
    const [url, setUrl] = useState("");

    const getData = async (query)=>{
        const {data, error, isLoading} = query(id);
        await data;
        return data as PokeProfile[];
    }
    const data = getData(useGetPokemonByIdQuery);

    const idAppender = (url) => {
        console.log((url + id + ".png"));
        return (url + id + ".png");
    }

    const urlReducer = (action) => {
        switch(action.type)
        {
            case 1:
                return "https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/versions/generation-i/red-blue/back/";
            case 2:
                return "https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/versions/generation-ii/crystal/";
            case 3:
                return "https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/versions/generation-iii/emerald/";
            case 4:
                return "https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/versions/generation-iv/diamond-pearl/";
            case 5:
                return "https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/versions/generation-v/black-white/";
            case 6:
                return "https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/versions/generation-vi/omegaruby-alphasapphire/";

            default:
                return "https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/";
        }
    }

    data.then((results)=>{
        if(results!==undefined)
        {
            setCachedData(results);

        }
    });

    //const {name,setName} = useState((data!==undefined) ? data.name : "undefined");

    //const number = (isLoading ? 0 : data.id);

    return (
        <div>
            <button onClick={()=>{setUrl(idAppender(urlReducer(1))); setGen(1);}}>1</button>
            <button>2</button>
            <button>3</button>
            <button>4</button>
            <button>5</button>
            <button>6</button>
        </div>
    );
}

export default Pokemon;
