import React from 'react';
import { useEffect, useState } from "react";
import { createApi, fetchBaseQuery } from '@reduxjs/toolkit/query/react'
import { PokeProfile } from './types';

export const pokemonApi = createApi({
  reducerPath: 'pokemonApi',
  baseQuery: fetchBaseQuery({ baseUrl: 'https://pokeapi.co/api/v2/' }),
  endpoints: builder => ({
    getPokemonById: builder.query({
      query: (id: string) => `pokemon/${id}`
    })
  })
})

export const { useGetPokemonByIdQuery } = pokemonApi

export const Pokemon = ({id, children})=>
{       
    const [cachedData, setCachedData] = useState({});
    const [gen, setGen] = useState(1);
    const [url, setUrl] = useState("");

    const getData = async (query)=>{
        const {data, error, isLoading} = query(id);
        await data;
        return data as PokeProfile[];
    }
    const data = getData(useGetPokemonByIdQuery);

    const idAppender = (url) => {
        console.log((url + id + ".png"));
    }

    const urlReducer = (setUrl, action) => {
        switch(action.type)
        {
            case 1:
                setUrl("https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/versions/generation-i/red-blue/back/");
                return;
            case 2:
                setUrl("https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/versions/generation-ii/crystal/");
                return;
            case 3:
                setUrl("https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/versions/generation-iii/emerald/");
                return;
            case 4:
                setUrl("https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/versions/generation-iv/diamond-pearl/");
                return;
            case 5:
                setUrl("https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/versions/generation-v/black-white/");
                return;
            case 6:
                setUrl("https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/versions/generation-vi/omegaruby-alphasapphire/");
                return;

            default:
                setUrl("https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/");
                return;
        }
    }

    data.then((results)=>{
        if(results!==undefined)
        {
            setCachedData(results);

        }
    });

    //const {name,setName} = useState((data!==undefined) ? data.name : "undefined");

    //const number = (isLoading ? 0 : data.id);

    return (
        <div>
            {url}
        </div>
    );
}

export default Pokemon;
