import * as React from 'react';
import {useState} from 'react';

import Button from '@mui/material/Button';
import TextField from '@mui/material/TextField';

import { Pokemon } from './features/Pokemon.tsx'

import '@fontsource/roboto/300.css';
import '@fontsource/roboto/400.css';
import '@fontsource/roboto/500.css';
import '@fontsource/roboto/700.css';

function App() {
  const [start, setStart] = useState(0);
  const [end, setEnd] = useState(5);

  const [textFieldMin, setTextFieldMin] = useState(0);
  const [textFieldMax, setTextFieldMax] = useState(0);

  const getArrayStub =(start, end)=>
  {
      /*
        I know this looks questionable, but hear me out;
        I'm making an array of n-many integers so I can map over it. Each index of our array is a pokemon.
        The data isn't baked, technically, so you could in theory load more than the original pokemon if you tried hard enough.
      */
      let arr=[(end-start)];
      for(let i=start; i<len; i++)
      {
        arr[i]=i+1;
      }
      return arr
  }

  return (
    <div className="App">
      <header className="App-header">

        <strong>Start range:</strong>
        <TextField style={{margin:"3em"}} onChange={(event)=>{setTextFieldMin(event.target.value)}}> </TextField>

        <strong>End range:</strong>
        <TextField style={{margin:"3em"}} onChange={(event)=>{setTextFieldMax(event.target.value)}}> </TextField>

        <Button style={{margin:"3em"}} variant="contained" onClick={()=>{
          setStart(Number.parseInt(textFieldMin));
          setEnd(Number.parseInt(textFieldMax));
        }}>Show me more pokemon!</Button>
        <br /><br />
        Currently, {(!isNaN(numPokemon)) ? numPokemon : 0} are set to be displayed.
        <br /><br />
        Note to the professor: Yes, this can load all 151 requested pokemon; and more!
        <br /><br />
        {
          getArrayStub(numPokemon).map(
            (num)=>{return <Pokemon key={num} id={num}></Pokemon>}
          )
        }


      </header>

    </div>
  );
}

export default App;
