import * as React from 'react';
import { useEffect, useState } from "react";

import { createApi, fetchBaseQuery } from '@reduxjs/toolkit/query/react'

import Button from '@mui/material/Button'
import Card from '@mui/material/Card';

import { Ability, PokeProfile, Type} from './types';



import '@fontsource/roboto/300.css';
import '@fontsource/roboto/400.css';
import '@fontsource/roboto/500.css';
import '@fontsource/roboto/700.css';

/*
    A non-null initial state for PokeProfile
*/
const initialState: PokeProfile = {
    abilities: [],
    base_experience: 0,
    forms: [],
    game_indices: [],
    height: 0,
    held_items: [],
    id: 0,
    is_default: false,
    location_area_encounters: "",
    moves: [],
    name: "",
    order: 0,
    past_types: [],
    species: {
        name:"",
        url:""
    },
    sprites: [],
    stats: [],
    types: [],
    weight: 0,
};

export const pokemonApi = createApi({
  reducerPath: 'pokemonApi',
  baseQuery: fetchBaseQuery({ baseUrl: 'https://pokeapi.co/api/v2/' }),
  endpoints: builder => ({
    getPokemonById: builder.query({
      query: (id: string) => `pokemon/${id}`
    })
  })
})
export const { useGetPokemonByIdQuery } = pokemonApi


export const Pokemon = ({id, children})=>
{           
    const {data, error, isLoading} = useGetPokemonByIdQuery(id);

    const [cachedData, setCachedData] = useState(initialState);
    const [isReady, setIsReady] = useState(false);

    const [gen, setGen] = useState(1);
    const [url, setUrl] = useState("");



    useEffect(()=>{
        setCachedData(initialState);
        (async()=>{await data})();
        setCachedData(data);
        setIsReady(true);
    },[data])




    /*

    [ ] User must be to view the sprite of each Pokemon in your Pokedex.
    [ ] User must be able to switch between the sprites from each generation (1-6).
    [ ] User must view basic information about the Pokemon (Number, Type, Height in Meters, Weight in Kilograms, Abilities)

                <div>Abilities: { isReady ? JSON.stringify(cachedData.abilities) : "Loading..."} </div>

    */

    return (

            <Card style={{backgroundColor:"lightgray" , padding:"0.4em", paddingBottom:"1.2em", margin:"3em", float:"left", width:"40vw"}}>
                <Card style={{padding:"1em"}}>
                    {(cachedData===undefined) ? "Loading..." : 
                    <>
                        <Button style={{margin:"0.4em", padding:"0.4em"}} variant="contained" onClick={()=>{setGen(1)}}></Button>
                        <Button variant="contained" onClick={()=>{setGen(2)}}></Button>
                        <Button variant="contained" onClick={()=>{setGen(3)}}></Button>
                        <Button variant="contained" onClick={()=>{setGen(4)}}></Button>
                        <Button variant="contained" onClick={()=>{setGen(5)}}></Button>
                        <Button variant="contained" onClick={()=>{setGen(6)}}></Button>
                        <div>Name: { isReady ? cachedData.name : "Loading..."} </div>
                        <div>Number: { isReady ? cachedData.id : "Loading..."} </div>
                        <div>Height: { isReady ? cachedData.height : "Loading..."} </div>
                        <div>Weight (kg): { isReady ? cachedData.weight : "Loading..."} </div>
                        <br />
                        <div>Abilities: {
                            cachedData.abilities.map(
                                (index: Ability)=>
                                {
                                    return <div key={index.slot}>{index.ability.name}</div>;
                                })
                            }
                        </div>
                        <br />
                        <div>Types:  {cachedData.types.map(
                            (index)=>{
                                return <span key={index.slot} style={{marginRight:"0.4em"}} className={"type ".concat(index.type.name)}></span>
                            }
                        )}</div>
                        <br />

                    </>
                    }
                </Card>
            </Card>

    );
}

export default Pokemon;
