import * as React from 'react';
import { useEffect, useState } from "react";

import { createApi, fetchBaseQuery } from '@reduxjs/toolkit/query/react'

import Button from '@mui/material/Button'
import Card from '@mui/material/Card';

import { PokeProfile} from './types';

import { PokeAbilities } from './PokeAbilities.tsx';
import { PokeTypes } from './PokeTypes.tsx';
import { PokeStats } from './PokeStats.tsx';
import { PokePics } from './PokePics.tsx';

/*
    A non-null initial state for PokeProfile
*/
const initialState: PokeProfile = {
    abilities: [],
    base_experience: 0,
    forms: [],
    game_indices: [],
    height: 0,
    held_items: [],
    id: 0,
    is_default: false,
    location_area_encounters: "",
    moves: [],
    name: "",
    order: 0,
    past_types: [],
    species: {
        name:"",
        url:""
    },
    sprites: [],
    stats: [],
    types: [],
    weight: 0,
};

/*
    The pokemon API we'll be querying from
    Node didn't seem to like having this in another file for some reason; it *absolutely refused* to detect it.
    Not sure as to why. For this reason, I've moved it to Pokemon.tsx as it seemed to be the next best option.
*/
export const pokemonApi = createApi({
  reducerPath: 'pokemonApi',
  baseQuery: fetchBaseQuery({ baseUrl: 'https://pokeapi.co/api/v2/' }),
  endpoints: builder => ({
    getPokemonById: builder.query({
      query: (id: string) => `pokemon/${id}`
    })
  })
})
export const { useGetPokemonByIdQuery } = pokemonApi




export const PokePanel = ({data})=> {
    const [gen, setGen] = useState(1);

    return (<div style={{marginTop: "-13em", float:"right"}}>
        <Button style={{margin:"0.4em", padding:"0.4em"}} variant="contained" onClick={()=>{setGen(1)}}>1</Button>
        <Button style={{margin:"0.4em", padding:"0.4em"}} variant="contained" onClick={()=>{setGen(2)}}>2</Button>
        <Button style={{margin:"0.4em", padding:"0.4em"}} variant="contained" onClick={()=>{setGen(3)}}>3</Button>
        <Button style={{margin:"0.4em", padding:"0.4em"}} variant="contained" onClick={()=>{setGen(4)}}>4</Button>
        <Button style={{margin:"0.4em", padding:"0.4em"}} variant="contained" onClick={()=>{setGen(5)}}>5</Button>
        <Button style={{margin:"0.4em", padding:"0.4em"}} variant="contained" onClick={()=>{setGen(6)}}>6</Button>

        <br />

        <PokePics gen={gen} data={data} />

    </div>);
}



export const Pokemon = ({id, children})=>
{           
    const {data, error, isLoading} = useGetPokemonByIdQuery(id);

    const [cachedData, setCachedData] = useState(initialState);
    const [isReady, setIsReady] = useState(false);


    const [url, setUrl] = useState("");

    useEffect(()=>{
        setCachedData(initialState);
        (async()=>{await data})();
        setCachedData(data);
        setIsReady(true);
    },[data])


    /*

    [ ] User must be to view the sprite of each Pokemon in your Pokedex.
    [ ] User must be able to switch between the sprites from each generation (1-6).
    [X] User must view basic information about the Pokemon (Number, Type, Height in Meters, Weight in Kilograms, Abilities)

    */

    return (

            <Card style={{backgroundColor:"lightgray" , padding:"0.4em", paddingBottom:"1.2em", margin:"3em", float:"left", width:"40vw"}}>
                <Card style={{padding:"1em"}}>
                    {(cachedData===undefined) ? "Loading..." : 
                    <>


                        <PokeStats data={cachedData} isReady={isReady} />
                        <PokeAbilities data={cachedData} />
                        <PokeTypes data={cachedData} />


                        <PokePanel data={cachedData} />
                    </>
                    }
                </Card>
            </Card>

    );
}

export default Pokemon;
