import React from 'react';
import {useState} from 'react';
import { Pokemon } from './features/Pokemon.tsx'

import '@fontsource/roboto/300.css';
import '@fontsource/roboto/400.css';
import '@fontsource/roboto/500.css';
import '@fontsource/roboto/700.css';


function getArrayStub(len)
{
    /*
      I know this looks questionable, but hear me out;
      I'm making an array of n-many integers so I can map over it. Each index of our array is a pokemon.
      The data isn't baked, technically, so you could in theory load more than the original pokemon if you tried hard enough.
    */
    let arr=[len];
    for(let i=0; i<len; i++)
    {
      arr[i]=i+1;
    }
    return arr
}

function App() {
  const [numPokemon, setNumPokemon] = useState(5);
  const [textField, setTextField] = useState(0);
  const [childrenElements, setChildrenElements] = useState();


  return (
    <div className="App">
      <header className="App-header">
        <input onChange={(event)=>{setTextField(event.target.value)}} type="text" desc="Number of pokemon you'd like to load: (Default 151, currently ${numPokemon}" name="numPokemon"></input>
        <button onClick={()=>{setNumPokemon(Number.parseInt(textField))}}>Submit</button>
        <br />
        Currently, {numPokemon} are set to be displayed.
        <br /><br />
        {
          getArrayStub(numPokemon).map(
            (num)=>{return <Pokemon key={num} id={num}></Pokemon>}
          )
        }
      </header>
    </div>
  );
}

export default App;
