export const getPokemon = async () => {
    const tempFunc = async ()=>{
        const response = await fetch("https://pokeapi.co/api/v2/pokemon?limit=151&offset=0");
        const json = await response.json();

        const data = (json.results) as Stub[];

        return data;
    };

    let data=(await tempFunc());

    let results:{}[] = [];

    for(let i = 0; i<data.length; i++)
    {
        const response = await fetch(data[i].url);
        const json = await response.json();

        /*
        On a notable technicality, this information isn't static or baked in.
        It's just grabbing *specifically the data we wanted* from our request.
        More of a filtering step than aything else.
        */
        results[i] = json;
    }

    return results;
}
